import React, { Component} from 'react';
import { Modal, Button, Form, Row, Col } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
var util = require('web3-utils');
// const [selectedInvoiceDate, setselectedInvoiceDate] = useState(null);
export default class CreateInvoice extends Component {
  constructor(props) {
    super(props);
    this.state = { address: '', pay: 0, type: '', hours: 0, perHour: true, perProject: false, label: '', description: '', selectedInvoiceDate: '', selectedFinanceDate: '', invoiceNumber: '', customerName: '', amount: 0};
  }
  submitInvoice = () => {
    const { address, pay, type, hours, label, description, perHour,selectedInvoiceDate, selectedFinanceDate, invoiceNumber, customerName, amount } = this.state;

    const sender_address = window.ethereum.selectedAddress;
    console.log("Sender address is : " + sender_address);

    const payload = {
      sender_address: sender_address.toLowerCase(),
      recipient_address: address.toLowerCase(),
      amount: amount,
      description,
      label,
      selectedInvoiceDate,
      selectedFinanceDate,
      invoiceNumber,
      customerName,
      amount
    }
    this.props.submitInvoice(payload);
    this.props.handleCloseDialog();
  }

  perHourToggle = (evt) => {
    this.setState({ perHour: evt.target.checked, perProject: !evt.target.checked })
  }

  perProjectToggle = (evt) => {
    this.setState({ perProject: evt.target.checked, perHour: !evt.target.checked });
  }

  hoursChanged = (evt) => {
    this.setState({ hours: evt.target.value });
  }

  payChanged = (evt) => {
    this.setState({ pay: evt.target.value });
  }

  managerAddressChanged = (evt) => {
    this.setState({ address: evt.target.value });
  }

  labelChanged = (evt) => {
    this.setState({ label: evt.target.value });
  }

  descriptionChanged = (evt) => {
    this.setState({ description: evt.target.value });
  }
  InvoiceDateChanged = (date) => {
    this.setState({selectedInvoiceDate : date});
  }
  FinanceDateChanged = (date) => {
    this.setState({selectedFinanceDate : date});
  }
  invoiceNumberChanged = (evt) => {
    this.setState({invoiceNumber : evt.target.value});
  }
  CustomerNameChanged = (evt) => {
    this.setState({customerName: evt.target.value});
  }
  amountChanged = (evt) => {
    this.setState({amount: evt.target.value});
  }

  render() {

    const { dialogVisible, handleCloseDialog, } = this.props;
    const { pay, description, perHour, perProject, hours, address, label, selectedInvoiceDate, selectedFinanceDate, invoiceNumber, customerName, amount} = this.state;


    let currentPayForm = <span/>;
    if (perHour) {
      currentPayForm =
        <div>
        <Form.Row>
          <Col>
                <Form.Control type="text" placeholder="Enter amount" value={pay} onChange={this.payChanged}/>
                <Form.Text className="text-muted">
                    Enter the amount (Ether)
                </Form.Text>
           </Col> 
         <Col>
                <Form.Control type="text" placeholder="Enter hours" value={hours} onChange={this.hoursChanged}/>
                <Form.Text className="text-muted">
                    Enter the hours
                </Form.Text>
          </Col>     
        </Form.Row>
    </div>
    }
    else {
      currentPayForm = <Form.Group>
          <Form.Control type="text" placeholder="Enter amount" value={pay} onChange={this.payChanged}/>
          <Form.Text className="text-muted">
              Enter the amount (Ether)
          </Form.Text>
      </Form.Group>
    }
    return (
      <div>
      <Modal show={dialogVisible} onHide={handleCloseDialog}>
        <Modal.Header closeButton>
          <Modal.Title>Submit a new invoice</Modal.Title>
        </Modal.Header>
        <Modal.Body>

<Form>
<div class="alert alert-danger" role="alert">
  Alert! You have already submitted an invoice discounting request with the same invoice number!
</div>
<Form.Group controlId="description">
   <Form.Label>Invoice Number</Form.Label>
    <Form.Control type="text" placeholder="Enter the unique invoice number" value={invoiceNumber} onChange={this.invoiceNumberChanged}/>

  </Form.Group> 

  <Form.Group controlId="formBasicEmail">
    <Form.Label>Enter the Account number of the Invoice Financing Company</Form.Label>
    <Form.Control type="text" placeholder="Enter wallet address" value={address} onChange={this.managerAddressChanged}/>
    <Form.Text className="text-muted" >
      Enter the public key of your manager's wallet
    </Form.Text>
  </Form.Group>


  <Form.Group controlId="description">
   <Form.Label>Customer's Name</Form.Label>
    <Form.Control type="text" placeholder="Who is the customer of the Goods/Services?" value={customerName} onChange={this.CustomerNameChanged}/>

    <Form.Group controlId="description">
   <Form.Label>Date as per invoice</Form.Label>
      <DatePicker selected={selectedInvoiceDate} onChange={this.InvoiceDateChanged}/>
  </Form.Group>  

  </Form.Group> 
  <Form.Group controlId="description">
   <Form.Label>Total Amount</Form.Label>
    <Form.Control type="text" placeholder="What is the total amount of Invoice" value={amount} onChange={this.amountChanged}/>

  </Form.Group> 

  <Form.Group controlId="description">
   <Form.Label>Label</Form.Label>
    <Form.Control type="text" placeholder="What is the invoice for?" value={label} onChange={this.labelChanged}/>

  </Form.Group>  

  <Form.Group controlId="description">
   <Form.Label>Date within which funds are needed</Form.Label>
      <DatePicker selected={selectedFinanceDate} onChange={this.FinanceDateChanged}/>
  </Form.Group>  

     {/* <Form.Group controlId="formBasicEmail">   
      <Form.Check  inline
        type={"radio"}
        id={`default-`}
        label={`Per Hour`}
        name="projectType"
        checked={perHour}
        onChange={this.perHourToggle}
      />
        <Form.Check  inline
        type={"radio"}
        id={`default-`}
        label={`Per Project`}
        checked={perProject}
        onChange={this.perProjectToggle}
      />
      </Form.Group> */}
      {/* {currentPayForm} */}
  {/* <Form.Group controlId="description">
    <Form.Label>Description/Comments</Form.Label>
    <Form.Control as="textarea" rows="3" value={description} onChange={this.descriptionChanged}/>
  </Form.Group>    */}
  </Form>

        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseDialog}>
            Cancel
          </Button>
          <Button variant="primary" onClick={this.submitInvoice}>
            Submit Invoice
          </Button>
        </Modal.Footer>
      </Modal>      
      </div>
    )
  }
}
