/**
 * An interface used to interact with dfuse REST API.
 *
 * Created mainly to abstract implementation details of dealing
 * with HTTP request/response, enable consumer of the library to
 * provide their own implementation of an HTTP client (think about
 * providing `Fetch` interface instead).
 *
 * @group Interfaces
 */
export interface HttpClient {
  /**
   * Release any resources hold by this [[HttpClient]] instance. Must
   * be tolerant to being called multiple times.
   *
   * Once called, the instance is assumed unsuable and should never
   * be invoked anymore.
   */
  release(): void

  /**
   * Make an anonymous request (unauthenticated) to the dfuse Authentication endpoint.
   * Usually used only for issuing an API token from an API key.
   *
   * @param path (required) The HTTP path on the endpoint
   * @param method (required) The HTTP method to perform the request agaisnt
   * @param params (defaults `{}`) The HTTP query parameters to append to the url, they will
   * be url-encoded and included in the final remote url. Has no effect when empty or undefined.
   * @param body (defaults `undefined`) The HTTP body to include in the request, assumed to be a
   * JSON object that will be serialized to a string. Not included in the HTTP request when `undefined`.
   * @param headers (defaults `{}`) The extra HTTP headers to include in the request. Those will be merged
   * with default ones (`{ Authorization: ... }`) and they override them if same key are specified.
   *
   * @returns A `Promise` that will resolve to the response body if it passes. Will reject with a
   * [[DfuseApiError]] if it fits the dfuse Error Format or a generic `DfuseError` is it's something
   * not fitting our expected format.
   */
  authRequest<T>(
    path: string,
    method: string,
    params?: HttpQueryParameters,
    body?: any,
    headers?: HttpHeaders
  ): Promise<T>

  /**
   * Make an authenticated request (unauthenticated) to the dfuse REST endpoint.
   *
   * Upon a succesfull call, the actual response body (expected to be a valid JSON) will
   * be returned to the caller.
   *
   * Upon an error, a [[DfuseError]] is returned, will be a [[DfuseApiError]] if the response's
   * body exist, it's a valid JSON string and it fits the dfuse error format.
   *
   * @param apiToken The API token used to to interact with the API endpoint. The token will be turned
   * into a proper HTTP header `Authorization: Bearer ...`.
   * @param path (required) The HTTP path on the endpoint
   * @param method (required) The HTTP method to perform the request agaisnt
   * @param params (defaults `{}`) The HTTP query parameters to append to the url, they will
   * be url-encoded and included in the final remote url. Has no effect when empty or undefined.
   * @param body (defaults `undefined`) The HTTP body to include in the request, assumed to be a
   * JSON object that will be serialized to a string. Not included in the HTTP request when `undefined`.
   * @param headers (defaults `{}`) The extra HTTP headers to include in the request. Those will be merged
   * with default ones (`{ Authorization: ... }`) and they override them if same key are specified.
   *
   * @returns A `Promise` that will resolve to the response body if it passes. Will reject with a
   * [[DfuseApiError]] if it fits the dfuse Error Format or a generic `DfuseError` is it's something
   * not fitting our expected format.
   */
  apiRequest<T>(
    apiToken: string,
    path: string,
    method: string,
    params?: HttpQueryParameters,
    body?: any,
    headers?: HttpHeaders
  ): Promise<T>
}

export type HttpHeaders = Record<string, string>
export type HttpQueryParameters = Record<string, any>

/**
 * This interface is the bare minimum as required by our internal usage.
 *
 * This is copied to ensure minimal compatiblity with `fetch` is required
 * and thus, it's required to provide a full clone of `fetch` handling.
 * To avoid that problem of over-complexifying , we define a small interface of what we really use
 * inside the library. It's the only part's that are needed.
 *
 * Passing the `window.fetch` (in the Browser) or `global.fetch` (polyfilled in Node.js)
 * should always be accepted as a valid usage.
 *
 * @ignore
 */
export type Fetch = (url: string, options?: RequestInit) => Promise<HttpResponse>

export type RequestInit = {
  body?: any
  headers?: any
  method?: string
}

/**
 * @ignore
 */
export interface HttpBody {
  json(): Promise<any>
  text(): Promise<string>
}

/**
 * @ignore
 */
export type HttpResponse = {
  readonly headers: any
  readonly ok: boolean
  readonly status: number
  readonly statusText: string
  readonly url: string
} & HttpBody
import debugFactory, { IDebugger } from "debug"
import { DfuseClientError, DfuseSocketError } from "../types/error"
import {
  WebSocket,
  Socket,
  SocketMessageListener,
  WebSocketFactory,
  SocketConnectOptions
} from "../types/socket"

export interface SocketOptions {
  /**
   * An id to assign to this socket instance. This is used through the `debug`
   * package to display a different `debug` tag when provided (i.e. `dfuse:socket:<id>`).
   *
   * @default `undefined`
   */
  id?: string

  /**
   * Determines if the socket should automatically re-connect with the upstream
   * service upon an abnormal disconnection.
   *
   * The re-connection happens after the given [[SocketOptions.reconnectDelayInMs]]
   * has elapsed.
   *
   * @default `true`
   */
  autoReconnect?: boolean

  /**
   * The delay after the abnormal closure of the connection before trying a
   * re-connection. As no effect if [[SocketOptions.autoReconnect]] is sets
   * to `false`.
   *
   * @default `2.5s` (See [[DEFAULT_RECONNECT_DELAY_IN_MS]])
   */
  reconnectDelayInMs?: number

  /**
   * Whether `ping` messages should be send automatically at regular interval
   * by the [[Socket]] instance. The actual interval used can be defined by
   * providing the [[SocketOptions.keepAliveIntervalInMs]] option.
   *
   * @default `true`
   */
  keepAlive?: boolean

  /**
   * The interval of time at which `ping` messages are automatically sent to
   * the remote endpoint. This is time that elapsed between two consecutives
   * `ping` messages.
   *
   * As no effect if [[SocketOptions.keepAlive]] is sets to `false`.
   *
   * @default `30s` (See [[DEFAULT_KEEP_ALIVE_INTERVAL_IN_MS]])
   */
  keepAliveIntervalInMs?: number

  /**
   * The set of webSocket protocols we want to send when performing the
   * connection handshake. For example, GraphQL subscription over WebSocket
   * uses "graphql-ws" protocol while standard WebSocket connection can
   * pass `undefined` as the value.
   *
   * @default `undefined`
   */
  webSocketProtocols?: string | string[]

  /**
   * A factory method used to create the `WebSocket` instance that should be
   * used by the [[Socket]] instance.
   *
   * **Inferrence**<br><br>
   * When not provided (default), the factory to use is actually inferred
   * based on the runtime environment.
   *
   * If a `window.WebSocket` variable exists, which should be the case on a Browser
   * environment, the factory used will call `new window.WebSocket(...)` to instantiate
   * the `WebSocket` instance.
   *
   * If a `global.WebSocket` variable exists, which can be the case upon a
   * `global.WebSocket = ...` call at the bootstrap phase in a Node.js environment,
   * the factory used will call `new global.WebSocket(...)` to instantiate
   * the `WebSocket` instance.
   *
   * Finally, if no `WebSocket` instance could be determined, a [[DfuseError]] is
   * thrown with a message explaining the situtation and a link to the documentation
   * on how to solve the problem.
   *
   * @default `undefined` (Inferred based on environment, see `Inferrence` note above)
   */
  webSocketFactory?: WebSocketFactory

  /**
   * A callback that can be provided to be notified when the [[Socket]] just performed
   * a re-connection. This will be called after a successful re-connection. When the callback
   * is invoked, the connection with the server has actually resumed and stream can be
   * restarted or other actions can be taken.
   *
   * This is a post re-connection callback.
   *
   * @default `() => {}` (noop)
   */
  onReconnect?: () => void

  /**
   * A callback that can be provided to be notified when the [[Socket]] receives an
   * error event. The error event is always emitted only where an error occurred with
   * the connection. It is not emitted on a normal and expected disconnection with
   * the server (when closing the underlying socket for example).
   *
   * The callback will be invoked with the actual event as defined by the `WebSocket`
   * instance used.
   *
   * **Important Note**<br><br>
   * The actual `event` object you will receive is different wheter you use
   * the Browser `WebSocket` instance ([ErrorEvent](https://developer.mozilla.org/en-US/docs/Web/API/WebSocket/error_event))
   * or depending on the Node.js library you use (If you use [ws](https://www.npmjs.com/package/ws) package, then it's
   * [ErrorEvent](https://github.com/websockets/ws/blob/master/lib/event-target.js#L87))
   *
   * @default `() => {}` (noop)
   */
  onError?: (event: object) => void

  /**
   * A callback that can be provided to be notified when the [[Socket]] receives a
   * close event. The close event is always emitted even when an error has occurred, it
   * will however always come **after** the error event.
   *
   * The callback will be invoked with the actual event as defined by the `WebSocket`
   * instance used.
   *
   * **Important Note**<br><br>
   * The actual `event` object you will receive is different wheter you use
   * the Browser `WebSocket` instance ([CloseEvent](https://developer.mozilla.org/en-US/docs/Web/API/WebSocket/close_event))
   * or depending on the Node.js library you use (If you use [ws](https://www.npmjs.com/package/ws) package, then it's
   * [CloseEvent](https://github.com/websockets/ws/blob/master/lib/event-target.js#L47))
   *
   * @default `() => {}` (noop)
   */
  onClose?: (event: object) => void
}

// @deprecated Please use SocketConnectOptions instead, will be removed eventually
export type ConnectOptions = SocketConnectOptions

/**
 * Create an actual [[Socket]] instance that will be used as the interface to wrap all
 * communicaton of the WebSocket protocol.
 *
 * @param url The url used to reach the dfuse Stream API, should **not** contain the `token` query parameter.
 * @param options The options used to configure the [[Socket]] instance, see [[SocketOptions]] for default options.
 */
export function createSocket(url: string, options: SocketOptions = {}): Socket {
  return new DefaultSocket(url, {
    id: "",
    autoReconnect: true,
    reconnectDelayInMs: DEFAULT_RECONNECT_DELAY_IN_MS,
    keepAlive: true,
    keepAliveIntervalInMs: DEFAULT_KEEP_ALIVE_INTERVAL_IN_MS,
    webSocketFactory: inferWebSocketFactory(
      options.id,
      options.webSocketProtocols,
      options.webSocketFactory
    ),
    ...options
  })
}

function inferWebSocketFactory(
  id: string | undefined,
  webSocketProtocols?: string | string[],
  webSocketFactory?: WebSocketFactory
): WebSocketFactory {
  const debug = debugFactory("dfuse:socket" + (id ? `:${id}` : ""))

  if (webSocketFactory !== undefined) {
    debug("Using user provided `webSocketFactory` option.")
    return webSocketFactory
  }

  // If we are in a Browser environment and `WebSocket` is available, use it
  if (typeof window !== "undefined" && (window as any).WebSocket != null) {
    debug("Using `WebSocket` global value found on 'window' variable (Browser environment).")
    return async (url: string) => new (window as any).WebSocket(url, webSocketProtocols)
  }

  // If we are in a Node.js like environment and `WebSocket` is available, use it
  if (typeof global !== "undefined" && (global as any).WebSocket != null) {
    debug("Using `WebSocket` global value found on 'global' variable (Node.js environment).")
    return async (url: string) => new (global as any).WebSocket(url, webSocketProtocols, {})
  }

  // Otherwise, throw an exception
  const messages = [
    "You did not provide a `webSocket` option and we were not able find a `WebSocket` object in",
    "the global scope to create use.",
    "",
    "You are most likely in a Node.js environment where a global `WebSocket` is not available by default.",
    "To resolve the issue, either pass a compatible `webSocket` option or globally defined a `global.WebSocket`",
    "variable pointing to a compatible `WebSocket` client.",
    "",
    "We invite you to read our documentation to learn more about this problem.",
    "",
    "See https://github.com/dfuse-io/client-js#nodejs"
  ]

  throw new DfuseClientError(messages.join("\n"))
}

const noop = () => {
  return
}

type Resolver<T> = (value?: T | PromiseLike<T>) => void
type Rejecter = (reason?: any) => void

export const DEFAULT_KEEP_ALIVE_INTERVAL_IN_MS = 30000 // 30s
export const DEFAULT_RECONNECT_DELAY_IN_MS = 2500 // 2.5s

class DefaultSocket implements Socket {
  private url: string
  private apiToken?: string
  private options: SocketOptions

  public isConnected: boolean = false
  public disconnectInitiator?: "client" | "server"
  public socket?: WebSocket

  private debug: IDebugger
  private listener?: SocketMessageListener
  private onReconnectListener?: () => void
  private onTerminationListener?: () => void
  private intervalHandler?: any

  private connectionPromise?: Promise<void>
  private closePromise?: Promise<void>
  private closeResolver?: Resolver<void>

  public constructor(url: string, options: SocketOptions) {
    this.url = url
    this.options = options

    this.debug = debugFactory("dfuse:socket" + (options.id !== "" ? `:${options.id}` : ""))
  }

  public setApiToken(apiToken: string): void {
    this.debug("Socket API token updated to %s.", apiToken)
    this.apiToken = apiToken
  }

  public async connect(
    listener: SocketMessageListener,
    options: SocketConnectOptions = {}
  ): Promise<void> {
    this.debug("About to connect to remote endpoint.")
    if (this.connectionPromise !== undefined) {
      return this.connectionPromise
    }

    if (this.isConnected) {
      return
    }

    this.listener = listener
    this.onReconnectListener = options.onReconnect
    this.onTerminationListener = options.onTermination

    this.connectionPromise = new Promise<void>((resolve, reject) => {
      this.debug("Connection promise started, creating and opening socket.")
      this.createAnOpenSocket(
        this.onSocketConnectOpenFactory(resolve),
        this.onSocketErrorFactory(reject),
        this.onSocketCloseFactory(),
        reject
      )
    })

    this.debug("Connection to remote endpoint in-progress, returning promise to caller.")
    return this.connectionPromise
  }

  public async disconnect(): Promise<void> {
    this.debug("About to disconnect from remote endpoint.")
    if (this.closePromise) {
      this.debug("A disconnect is already in progress, joining it for termination.")
      return this.closePromise
    }

    if (this.socket === undefined) {
      return
    }

    this.onReconnectListener = undefined
    this.listener = undefined

    this.debug("Closing socket.")
    this.socket.close()

    // We must not clean up the socket at this point yet. Cleaning up the socket means
    // removing the actual event listeners. If you clean up just yet, the `onclose` event
    // will not be handled and the consumer of the library does not receive its own
    // `onClose` event. Instead, let's do the clean up once we receive the socket
    // `onclose` event.

    this.debug(
      "Lazily disconnected, remaining clean up shall be performed when receiving `onclose` event."
    )
    this.isConnected = false
    this.disconnectInitiator = "client"

    this.closePromise = new Promise((resolve) => {
      // Shall be resolved by the `onClose` event handler on this class
      this.closeResolver = resolve
    })

    return this.closePromise
  }

  public async send<T = unknown>(message: T): Promise<void> {
    if (!this.isConnected) {
      this.debug("Not connected, re-connecting prior sending message.")
      await this.reconnect()
    }

    if (!this.isConnected) {
      this.debug("Socket not connected, unable to send message correctly.")
      throw new DfuseSocketError("Socket not connected, unable to send message correctly.")
    }

    this.debug("Sending message %o through socket.", message)
    this.socket!.send(JSON.stringify(message))
  }

  private async createAnOpenSocket(
    onSocketOpen: () => void,
    onSocketError: (event: Event) => void,
    onSocketClose: (event: CloseEvent) => void,
    onSocketFactoryError: (error: any) => void
  ): Promise<void> {
    const url = this.buildUrl()

    this.debug("Starting connection handshake with remote url %s.", url)
    try {
      const socket: WebSocket = await this.options.webSocketFactory!(url)

      socket.onopen = onSocketOpen
      socket.onerror = onSocketError
      socket.onclose = onSocketClose
      socket.onmessage = this.onSocketMessage

      this.socket = socket
    } catch (error) {
      onSocketFactoryError(error)
    }
  }

  private buildUrl(): string {
    if (this.apiToken === undefined) {
      // FIXME: Shall we throw an exception instead?
      return this.url
    }

    // FIXME: Decompose url, add query param api token if available, then re-compose url!
    if (this.url.includes("?")) {
      return `${this.url}&token=${this.apiToken}`
    }

    return `${this.url}?token=${this.apiToken}`
  }

  private onSocketConnectOpenFactory = (resolve: Resolver<void>) => () => {
    this.debug("Received `onopen` (via connect) notification from socket.")
    this.isConnected = true
    this.connectionPromise = undefined

    if (this.keepAliveOption() === true) {
      this.registerKeepAliveHandler()
    }

    this.debug("Signaling completion of `connect` method in the outer scope.")
    resolve()
  }

  private onSocketReconnectOpenFactory = (resolve: Resolver<boolean>) => () => {
    this.debug(
      "Received `onopen` (via reconnect) notification from socket with ready state %d.",
      this.socket!.readyState
    )
    this.isConnected = true
    this.connectionPromise = undefined

    if (this.keepAliveOption() === true) {
      this.registerKeepAliveHandler()
    }

    this.debug("Signaling completion of `reconnect` method in the outer scope.")
    resolve(true)

    this.debug("Sending a `onReconnect` notification to client consumer.")
    this.onReconnect()
  }

  private onSocketErrorFactory = (reject: Rejecter) => (event: Event) => {
    this.debug("Received `onerror` notification from socket (%s).", (event as any).error)

    // The official WebSocket flow is to always send an `onclose` event after an `onerror`
    // ones, as such, we must not clean the socket at this point. We must always
    // wait and ensures the `onclose` event will be called and that clean up will
    // happen in the `onclose` handler.

    this.debug("Signaling rejection of connection in the outer scope.")
    reject(event)

    this.debug("Sending an `onError` notification to client consumer.")
    this.onError(event)
  }

  private onSocketReconnectErrorFactory = () => (event: Event) => {
    this.debug("Received `onerror` (via reconnect) notification from socket.")

    this.debug("Sending an `onError` notification to client consumer.")
    this.onError(event)
  }

  private onSocketCloseFactory = () => {
    return this.commonOnSocketCloseFactory("connect", () => {
      this.reconnect().catch((error) => {
        this.debug("The re-connection never succeed, will not retry anymore.", error)
      })
    })
  }

  private onSocketReconnectCloseFactory = (resolve: Resolver<void>, reject: Rejecter) => {
    return this.commonOnSocketCloseFactory("reconnect", () => {
      this.tryReconnect(resolve, reject)
    })
  }

  private commonOnSocketCloseFactory = (
    tag: "connect" | "reconnect",
    reconnectWorker: () => void
  ) => (event: CloseEvent) => {
    this.debug("Received `onclose` (via %s) notification from socket.", tag)
    this.isConnected = false
    this.connectionPromise = undefined

    if (this.closeResolver) {
      this.debug("Resolving disconnect close promise (via %s).", tag)
      this.closeResolver()
      this.closeResolver = undefined
      this.closePromise = undefined
    }

    this.cleanSocket()

    this.debug(
      "Sending a `onClose` (%d) notification to client consumer (via %s).",
      event.code,
      tag
    )
    this.onClose(event)

    if (
      event.code !== 1000 &&
      event.code !== 1009 &&
      this.disconnectInitiator !== "client" &&
      this.options.autoReconnect
    ) {
      this.debug(
        "Socket has close abnormally (via %s), trying to re-connect to socket (infinite retry).",
        tag
      )

      reconnectWorker()
    } else {
      this.debug("Terminating socket lifecycle (via %s), no reconnection will be attempted.", tag)

      if (this.onTerminationListener) {
        this.onTerminationListener()
      }

      this.disconnectInitiator = undefined
    }
  }

  private onSocketMessage = (event: MessageEvent) => {
    let message: any
    try {
      message = JSON.parse(event.data) as { [key: string]: any }
    } catch (error) {
      this.debug("Received a non-JSON message, are you sure you are talking to dfuse API?")
      return
    }

    if (this.listener) {
      this.listener(message)
    }
  }

  private registerKeepAliveHandler() {
    const keepAliveInterval =
      this.options.keepAliveIntervalInMs || DEFAULT_KEEP_ALIVE_INTERVAL_IN_MS

    this.debug("Unregistering keep alive interval")
    this.intervalHandler = setInterval(() => {
      if (!this.isConnected || this.socket === undefined) {
        return
      }

      this.debug("Sending keep alive pong through socket.")
      this.socket.send(JSON.stringify({ type: "pong" }))
    }, keepAliveInterval)
  }

  private unregisterKeepAliveHandler() {
    if (this.intervalHandler === undefined) {
      return
    }

    this.debug("Unregistering keep alive interval")
    clearInterval(this.intervalHandler)
    this.intervalHandler = undefined
  }

  private async reconnect(): Promise<boolean> {
    this.debug("Reconnect has been invoked, perfoming initial re-connection logic.")
    if (this.connectionPromise) {
      try {
        this.debug("Awaiting actual connection to complete.")
        await this.connectionPromise
        return true
      } catch (error) {
        this.debug("Original connection did not succeed, continuing re-connection process.")
      }
    }

    if (!this.options.autoReconnect) {
      this.debug("Not re-connecting because client consumer auto reconnect option is falsy.")
      return false
    }

    return new Promise<boolean>(this.tryReconnect)
  }

  private tryReconnect = (resolve: any, reject: any) => {
    let reconnectDelay = this.options.reconnectDelayInMs
    if (reconnectDelay === undefined) {
      reconnectDelay = DEFAULT_RECONNECT_DELAY_IN_MS
    }

    this.debug(`Waiting ${reconnectDelay}ms before trying to perform the re-connection.`)
    setTimeout(() => {
      this.createAnOpenSocket(
        this.onSocketReconnectOpenFactory(resolve),
        this.onSocketReconnectErrorFactory(),
        this.onSocketReconnectCloseFactory(resolve, reject),
        reject
      )
    }, reconnectDelay)
  }

  private cleanSocket() {
    if (this.intervalHandler !== undefined) {
      this.unregisterKeepAliveHandler()
    }

    if (this.socket === undefined) {
      return
    }

    this.socket.onopen = noop
    this.socket.onclose = noop
    this.socket.onerror = noop
    this.socket.onmessage = noop

    this.socket = undefined
  }

  /**
   * We notify both the `onReconnect` option passed when constructing
   * the socket and the one (if present) that was passed when connecting
   * the socket.
   *
   * Those are two different listeners and must be both sent when possible.
   */
  private onReconnect() {
    // Let's call the `connect` `onReconnectListener` first then followed
    // by the one the consumer of the socket passed
    if (this.onReconnectListener) this.onReconnectListener()
    if (this.options.onReconnect) this.options.onReconnect()
  }

  private onClose(message: any) {
    if (this.options.onClose) this.options.onClose(message)
  }

  private onError(message: any) {
    if (this.options.onError) this.options.onError(message)
  }

  private keepAliveOption(): boolean {
    return this.options.keepAlive === undefined ? true : this.options.keepAlive
  }
}
import { ProgressData } from "../types/progress"
import { ActionTraceData } from "../types/action-trace"
import { ErrorData } from "../types/error"
import { ListeningData } from "../types/listen"
import { HeadInfoData } from "../types/head-info"
import { TableDeltaData } from "../types/table-delta"
import { TableSnapshotData } from "../types/table-snapshot"
import { TransactionLifecycleData } from "../types/transaction"

export type InboundMessage<T = any> = {
  type: InboundMessageType
  req_id?: string
  data: T
}

// **Important** The key must be the same as the API type but in upper snake case for "in" operation to work
export enum InboundMessageType {
  ACTION_TRACE = "action_trace",
  ERROR = "error",
  LISTENING = "listening",
  HEAD_INFO = "head_info",
  PING = "ping",
  PROGRESS = "progress",
  UNLISTENED = "unlistened",
  TABLE_DELTA = "table_delta",
  TABLE_SNAPSHOT = "table_snapshot",
  TRANSACTION_LIFECYCLE = "transaction_lifecycle"
}

export interface ActionTraceInboundMessage<T = Record<string, any>>
  extends InboundMessage<ActionTraceData<T>> {}
export interface ErrorInboundMessage extends InboundMessage<ErrorData> {}
export interface ListeningInboundMessage extends InboundMessage<ListeningData> {}
export interface HeadInfoInboundMessage extends InboundMessage<HeadInfoData> {}
export interface PingInboundMessage extends InboundMessage<{}> {}
export interface ProgressInboundMessage extends InboundMessage<ProgressData> {}
export interface UnlistenedInboundMessage extends InboundMessage<{}> {}
export interface TableDeltaInboundMessage<T = Record<string, any>>
  extends InboundMessage<TableDeltaData<T>> {}
export interface TableSnapshotInboundMessage<T = Record<string, any>>
  extends InboundMessage<TableSnapshotData<T>> {}
export interface TransactionLifecycleInboundMessage
  extends InboundMessage<TransactionLifecycleData> {}

// export type InboundMessageType = "action_trace"
//                                | "error"
//                                | "listening"
//                                | "head_info"
//                                | "ping"
//                                | "progress"
//                                | "unlistened"
//                                | "table_delta"
//                                | "table_snapshot"
//                                | "transaction_lifecycle"
export class Deferred<T> {
  private activePromise: Promise<T>
  private resolver!: (value?: T | PromiseLike<T>) => void
  private rejecter!: (reason?: any) => void

  constructor() {
    this.activePromise = new Promise((resolve, reject) => {
      this.resolver = resolve
      this.rejecter = reject
    })
  }

  public promise(): Promise<T> {
    return this.activePromise
  }

  public resolve(value?: T | PromiseLike<T>) {
    this.resolver(value)
  }

  public reject(reason?: any) {
    this.rejecter(reason)
  }
}
import debugFactory, { IDebugger } from "debug"
import { SocketOptions, createSocket } from "./socket"
import { OutboundMessage, unlistenMessage } from "../message/outbound"
import { InboundMessage, InboundMessageType } from "../message/inbound"
import { DfuseClientError } from "../types/error"
import { StreamClient, OnStreamMessage, OnStreamRestart } from "../types/stream-client"
import { Socket } from "../types/socket"
import { Stream, StreamMarker } from "../types/stream"
import { Deferred } from "../helpers/promises"

/**
 * The set of options that can be used when constructing a the default
 * [[StreamClient]] instance through the [[createStreamClient]] factory
 * method.
 */
export interface StreamClientOptions {
  /**
   * The [[Socket]] instance to use, inferred based on the environment when not provided.
   *
   * @default `undefined` (Inferred based on runtime environment (Node.js/Browser), see [[createSocket]])
   */
  socket?: Socket

  /**
   * The [[SocketOptions]] to pass when creating the default [[Socket]] instance.
   * This field has no effect if you provide yourself a [[StreamClientOptions.socket]] option.
   *
   * @default `undefined` (See [[SocketOptions]] for actual defaults this generates)
   */
  socketOptions?: SocketOptions

  /**
   * Determines all streams should automatically restart when the socket disconnects. The stream
   * will re-connect at their latest marked value (See [[Stream.mark]]) if present or at current
   * block if it was never marked.
   *
   * @default `true`
   */
  autoRestartStreamsOnReconnect?: boolean
}

/**
 * Create the default [[StreamClient]] concrete implementation.
 *
 * @param wsUrl The url used to reach the dfuse Stream API, should **not** contain the `token` query parameter. Passed as
 * is to created [[Socket]] interface through the [[createSocket]] factory method. This parameter has no effect
 * if [[StreamClientOptions.socket]] options is used.
 * @param options The set of options used to construct the default [[StreamClient]] instance. See
 * [[StreamClientOptions]] for documentation of the options and default values for each of them.
 */
export function createStreamClient(wsUrl: string, options: StreamClientOptions = {}): StreamClient {
  return new DefaultStreamClient(
    options.socket || createSocket(wsUrl, { id: "stream", ...options.socketOptions }),
    options.autoRestartStreamsOnReconnect === undefined
      ? true
      : options.autoRestartStreamsOnReconnect
  )
}

class DefaultStreamClient {
  private socket: Socket
  private autoRestartStreamsOnReconnect: boolean
  private debug: IDebugger = debugFactory("dfuse:stream")
  private debugTrace: IDebugger = debugFactory("dfuse-trace:stream")

  private streams: { [id: string]: DefaultStream } = {}

  constructor(socket: Socket, autoRestartStreamsOnReconnect: boolean) {
    this.socket = socket
    this.autoRestartStreamsOnReconnect = autoRestartStreamsOnReconnect
  }

  public release(): void {
    this.debug("Releasing default stream client")
    this.socket.disconnect().catch((error) => {
      this.debug(
        "An error occurred while disconnecting from socket while releasing instance",
        error
      )
    })
  }

  public setApiToken(apiToken: string) {
    this.socket.setApiToken(apiToken)
  }

  public async registerStream(
    message: OutboundMessage,
    onMessage: OnStreamMessage
  ): Promise<Stream> {
    if (!this.socket.isConnected) {
      this.debug("Socket is not connected, connecting socket first.")
      await this.socket.connect(this.handleMessage, { onReconnect: this.handleReconnection })
    }

    const id = message.req_id
    if (this.streams[id] !== undefined) {
      throw new DfuseClientError(
        `A stream with id '${id}' is already registered, cannot register another one with the same id`
      )
    }

    this.debug("Registering stream [%s] with message %o.", id, message)
    const streamExists = (streamId: string) => this.streams[streamId] !== undefined
    const unregisterStream = (streamId: string) => this.unregisterStream(streamId)
    const stream = new DefaultStream(
      id,
      message,
      onMessage,
      streamExists,
      unregisterStream,
      this.socket,
      this.debug.extend(id, ":")
    )

    // Let's first register stream to ensure that if messages arrives before we got back
    // execution flow after `send` call, the listener is already present to handle message
    this.streams[id] = stream

    try {
      await stream.start()
    } catch (error) {
      delete this.streams[id]
      throw new DfuseClientError(`Unable to correctly register stream '${id}'`, error)
    }

    this.debug("Stream [%s] registered with remote endpoint.", id)
    return stream
  }

  public async unregisterStream(id: string): Promise<void> {
    if (this.streams[id] === undefined) {
      this.debug("Stream [%s] is already unregistered, nothing to do.", id)
      return
    }

    const message = unlistenMessage({ req_id: id })
    this.debug("Unregistering stream [%s] with message %o.", id, message)

    delete this.streams[id]

    if (this.socket.isConnected) {
      await this.socket.send(message)
    }

    if (Object.keys(this.streams).length <= 0) {
      this.debug("No more stream present, disconnecting socket.")
      if (this.socket.isConnected) {
        await this.socket.disconnect()
      }
    }
  }

  private handleMessage = (rawMessage: unknown) => {
    const message = rawMessage as InboundMessage

    if (message.type === "ping") {
      this.debug("Discarding 'ping' message from reaching the underlying stream(s).")
      return
    }

    let debug = this.debugTrace
    if (
      message.type === InboundMessageType.ERROR ||
      message.type === InboundMessageType.LISTENING ||
      message.type === InboundMessageType.UNLISTENED
    ) {
      debug = this.debug
    }

    debug(
      "Routing socket message of type '%s' with req_id '%s' to appropriate stream",
      message.type,
      message.req_id
    )
    const stream = this.streams[message.req_id || ""]
    if (stream === undefined) {
      this.debug(
        "No stream currently registered able to handle message with req_id '%s'",
        message.req_id
      )
      return
    }

    stream.onMessage(message, stream)
  }

  private handleReconnection = () => {
    if (this.autoRestartStreamsOnReconnect === false) {
      return
    }

    Object.keys(this.streams).forEach((streamId) => {
      this.streams[streamId].restart()
    })
  }
}

class DefaultStream implements Stream {
  public readonly id: string
  public onPostRestart?: OnStreamRestart

  private activeMarker?: StreamMarker
  private activeJoiner?: Deferred<void>
  private registrationMessage: OutboundMessage
  private onMessageHandler: OnStreamMessage
  private unregisterStream: (id: string) => Promise<void>
  private streamExists: (id: string) => boolean
  private socket: Socket
  private debug: IDebugger

  constructor(
    id: string,
    registrationMessage: OutboundMessage,
    onMessage: OnStreamMessage,
    streamExists: (id: string) => boolean,
    unregisterStream: (id: string) => Promise<void>,
    socket: Socket,
    debug: IDebugger
  ) {
    this.id = id
    this.registrationMessage = registrationMessage
    this.onMessageHandler = onMessage
    this.streamExists = streamExists
    this.unregisterStream = unregisterStream
    this.socket = socket
    this.debug = debug
  }

  public get onMessage(): OnStreamMessage {
    return this.onMessageHandler
  }

  public currentActiveMarker(): StreamMarker | undefined {
    return this.activeMarker
  }

  public async start(): Promise<void> {
    return this.socket.send(this.registrationMessage)
  }

  public async restart(marker?: StreamMarker): Promise<void> {
    this.checkMarker(marker)

    if (!this.streamExists(this.id)) {
      throw new DfuseClientError(
        `Trying to restart a stream '${
          this.id
        }' that is not registered anymore or was never registered`
      )
    }

    let activeMarker = this.activeMarker
    if (marker) {
      activeMarker = marker
    }

    const restartMessage = { ...this.registrationMessage }
    if (activeMarker) {
      restartMessage.start_block = (activeMarker as any).atBlockNum
    }

    await this.socket.send(restartMessage)

    if (this.onPostRestart) {
      this.onPostRestart()
    }
  }

  public async join(): Promise<void> {
    if (this.activeJoiner !== undefined) {
      return this.activeJoiner.promise()
    }

    this.activeJoiner = new Deferred()

    return this.activeJoiner.promise()
  }

  public mark(marker: StreamMarker) {
    this.activeMarker = this.checkMarker(marker)
  }

  public async close(options: { error?: Error } = {}): Promise<void> {
    return (
      this.unregisterStream(this.id)
        .then(() => {
          if (options.error) {
            this.reject(options.error)
          } else {
            this.resolve()
          }
        })
        // FIXME: We should probably return a MultiError of some kind to report both error if `options.error` exists
        .catch(this.reject)
    )
  }

  private checkMarker(marker?: StreamMarker): StreamMarker | undefined {
    if (!marker) {
      return undefined
    }

    if (!(marker as any).atBlockNum || (marker as any).atBlockNum < 0) {
      throw new DfuseClientError(
        "Only non-zero & positive `atBlockNum` markers are accepted for this operation"
      )
    }

    return marker
  }

  private resolve = () => {
    if (this.activeJoiner) {
      this.debug("Resolving joiner promise.")
      this.activeJoiner.resolve()
      this.activeJoiner = undefined
    }
  }

  private reject = (error: Error) => {
    if (this.activeJoiner) {
      this.debug("Rejecting joiner promise with error %o.", error)
      this.activeJoiner.reject(error)
      this.activeJoiner = undefined
    }
  }
}
export function waitFor(ms: number) {
  return new Promise((resolve) => setTimeout(resolve, ms))
}
